import React, { useState } from "react";
import "./MatchJobs.css";

function MatchJobs() {
  const [cvFile, setCvFile] = useState(null);
  const [result, setResult] = useState(null);

  const handleUpload = (e) => {
    setCvFile(e.target.files[0]);
  };

  const handleMatch = () => {
    // Simulare răspuns
    const simulatedJob = {
      title: "Backend Developer – Banking",
      score: 92,
      industry: 100,
      skills: 88,
      semantic: 93,
    };
    setResult(simulatedJob);
  };

  return (
    <div className="match-jobs">
      <h2>Match Jobs</h2>
      <input type="file" accept=".txt,.docx" onChange={handleUpload} />
      <button onClick={handleMatch}>Find Best Matching Job</button>

      {result && (
        <div className="result-card">
          <h3>{result.title}</h3>
          <p>Total Score: {result.score}%</p>
          <p>Industry: {result.industry}%</p>
          <p>Skills: {result.skills}%</p>
          <p>Semantic: {result.semantic}%</p>
          <p className="explanation">CV matched this job due to high relevance in domain and skillset.</p>
        </div>
      )}
    </div>
  );
}

export default MatchJobs;
